import React, { useState } from "react";

const Sidebar = ({
  notes,
  onAddNote,
  onDeleteNote,
  activeNote,
  setActiveNote,
  onUpdateNoteFont,
  onUpdateNoteColor,
  onUpdateTheme,
}) => {
  const [title, setTitle] = useState("Untitled Note");
  const [font, setFont] = useState("Arial");
  const [color, setColor] = useState("black");
  const [theme, setTheme] = useState("light");

  const sortedNotes = notes.sort((a, b) => b.lastModified - a.lastModified);

  const changeFont = (newFont) => {
    setFont(newFont);
    onUpdateNoteFont(activeNote, newFont);
  };

  const changeColor = (newColor) => {
    setColor(newColor);
    onUpdateNoteColor(activeNote, newColor);
  };

  const changeTheme = (newTheme) => {
    setTheme(newTheme);
    onUpdateTheme(newTheme);
  };

  return (
    <div className={`app-sidebar ${theme === "dark" ? "dark-theme" : ""}`}>
      <div className="app-sidebar-header">
        <h1>Notes</h1>
        <button onClick={onAddNote}>Add</button>
      </div>
      <div className="app-sidebar-notes">
        <div className="app-sidebar-note">
          <div className="sidebar-note-title">
            <strong>
              <input
                type="text"
                id="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                style={{ fontFamily: font, color: color }}
              />
            </strong>
            <button onClick={() => onAddNote()}>Save</button>
            <button className="change-font" onClick={() => changeFont("Arial")}>
              Arial
            </button>
            <button
              className="change-font"
              onClick={() => changeFont("Times New Roman")}
            >
              Times New Roman
            </button>
            <button className="change-color" onClick={() => changeColor("black")}>
              Black
            </button>
            <button className="change-color" onClick={() => changeColor("red")}>
              Red
            </button>
            <button onClick={() => changeTheme("light")}>Light Theme</button>
            <button onClick={() => changeTheme("dark")}>Dark Theme</button>
          </div>
        </div>
        {sortedNotes.map(({ id, title, body, lastModified }, i) => (
          <div
            className={`app-sidebar-note ${id === activeNote && "active"}`}
            onClick={() => setActiveNote(id)}
            key={id}
          >
            <div className="sidebar-note-title">
              <strong>{title}</strong>
              <button onClick={() => onDeleteNote(id)}>Delete</button>
            </div>
            <p>{body && body.substr(0, 100) + "..."}</p>
            <small className="note-meta">
              Last Modified{" "}
              {new Date(lastModified).toLocaleDateString("en-GB", {
                hour: "2-digit",
                minute: "2-digit",
              })}
            </small>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
